def card_list(menu):
    result = ''
    for value in data :
         if value['category'] == menu:
            result = result + f'''
    <div class="col">
       <div class="card" style="width: 18rem;">
    <img src="{value['url']}" class="card-img-top" alt="...">
    <div class="card-body">
    <h5 class="card-title">{value['name']}</h5>
    <p class="card-text">{value['Achievements']}</p>
    </div>
    <ul class="list-group list-group-flush">
    <li class="list-group-item">{value['country']}</li>
    </ul>s
    </div>
    </div>
    '''
    return result

